async function handleLocationInput(inputElement, suggestionsElement, isStartLocation) {
            const query = inputElement.value.trim();
            suggestionsElement.innerHTML = '';
            suggestionsElement.style.display = 'none';

            if (isStartLocation) {
                if (state.startLocation && state.startLocation.address !== query) {
                    state.startLocation = null;
                }
            } else {
                 if (state.selectedDeliveryLocation && state.selectedDeliveryLocation.address !== query) {
                    state.selectedDeliveryLocation = null;
                    elements.addStopBtn.disabled = true;
                }
            }

            if (query.length < 3) return;

            if (!state.apiKey) {
                const noApiDiv = document.createElement('div');
                noApiDiv.className = 'suggestion-item no-results';
                noApiDiv.textContent = 'Please set Google Maps API key in Options.';
                suggestionsElement.appendChild(noApiDiv);
                suggestionsElement.style.display = 'block';
                return;
            }

            if (!autocompleteService) {
                console.error("Google Maps AutocompleteService not initialized.");
                const noApiDiv = document.createElement('div');
                noApiDiv.className = 'suggestion-item no-results';
                noApiDiv.textContent = 'Autocomplete service is not ready.';
                suggestionsElement.appendChild(noApiDiv);
                suggestionsElement.style.display = 'block';
                return;
            }

            autocompleteService.getPlacePredictions({ 
                input: query,
                componentRestrictions: { country: 'gb' }
            }, (predictions, status) => {
                if (status === google.maps.places.PlacesServiceStatus.OK && predictions) {
                    displaySuggestions(predictions, inputElement, suggestionsElement, isStartLocation);
                } else if (status === google.maps.places.PlacesServiceStatus.ZERO_RESULTS) {
                    const noResultDiv = document.createElement('div');
                    noResultDiv.className = 'suggestion-item no-results';
                    noResultDiv.textContent = 'No results found.';
                    suggestionsElement.appendChild(noResultDiv);
                    suggestionsElement.style.display = 'block';
                } else if (status === google.maps.places.PlacesServiceStatus.REQUEST_DENIED) {
                     alert(`Google Maps API Error: Autocomplete request denied. Please check your API key and ensure the Places API is enabled.`);
                } else {
                    console.error('Google Autocomplete Service Error:', status);
                }
            });
        }

function displaySuggestions(predictions, inputElement, suggestionsElement, isStartLocation) {
            suggestionsElement.innerHTML = '';
            if (predictions.length === 0) {
                suggestionsElement.style.display = 'none';
                return;
            }

            predictions.forEach(prediction => {
                const div = document.createElement('div');
                div.className = 'suggestion-item';
                div.textContent = prediction.description;
                div.addEventListener('click', () => {
                    inputElement.value = prediction.description;
                    suggestionsElement.style.display = 'none';

                    if (!placesService) {
                        alert("Places service not ready. Cannot get location details.");
                        return;
                    }

                    placesService.getDetails({
                        placeId: prediction.place_id,
                        fields: ['geometry', 'formatted_address', 'name']
                    }, (place, status) => {
                        if (status === google.maps.places.PlacesServiceStatus.OK && place && place.geometry && place.geometry.location) {
                            const locationData = {
                                address: place.formatted_address,
                                coordinates: [place.geometry.location.lat(), place.geometry.location.lng()],
                                notes: ''
                            };

                            if (isStartLocation) {
                                state.startLocation = locationData;
                                elements.startLocationInput.value = locationData.address;
                                updateMapMarkers();
                                updateCalculateRouteButton();
                            } else {
                                state.selectedDeliveryLocation = locationData;
                                elements.deliveryLocationInput.value = locationData.address;
                                elements.addStopBtn.disabled = false;
                            }
                        } else {
                            console.error("Error getting place details:", status);
                            alert("Could not retrieve location details. Please try again.");
                        }
                    });
                });
                suggestionsElement.appendChild(div);
            });

            suggestionsElement.style.display = 'block';
        }

function debounce(func, wait) {
            let timeout;
            return function executedFunction(...args) {
                const later = () => {
                    clearTimeout(timeout);
                    func(...args);
                };
                clearTimeout(timeout);
                timeout = setTimeout(later, wait);
            };
        }

function toggleSideMenu() {
            elements.sideMenu.classList.toggle('visible');
        }

function getUpdatedCurrentLocation() {
            return new Promise((resolve, reject) => {
                if (!navigator.geolocation) {
                    return reject("Geolocation is not supported by your browser");
                }

                navigator.geolocation.getCurrentPosition(
                    async (position) => {
                        const { latitude, longitude } = position.coords;
                        try {
                            const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`);
                            const data = await response.json();
                            let address = data.display_name || `Current Location (${latitude.toFixed(4)}, ${longitude.toFixed(4)})`;
                            const locationData = {
                                address: address,
                                coordinates: [latitude, longitude],
                                notes: state.startLocation?.notes || '' // preserve notes if possible
                            };
                            resolve(locationData);
                        } catch (error) {
                            // If reverse geocoding fails, just use coordinates
                            const locationData = {
                                address: `Current Location (${latitude.toFixed(4)}, ${longitude.toFixed(4)})`,
                                coordinates: [latitude, longitude],
                                notes: state.startLocation?.notes || ''
                            };
                            resolve(locationData);
                        }
                    },
                    (error) => {
                        let errorMessage = "Unable to retrieve your location";
                        switch(error.code) {
                            case error.PERMISSION_DENIED:
                                errorMessage = "Location access denied. Please enable location services in your browser settings.";
                                break;
                            case error.POSITION_UNAVAILABLE:
                                errorMessage = "Location information unavailable.";
                                break;
                            case error.TIMEOUT:
                                errorMessage = "Location request timed out.";
                                break;
                        }
                        reject(errorMessage);
                    },
                    {
                        enableHighAccuracy: true,
                        timeout: 10000,
                        maximumAge: 0
                    }
                );
            });
        }

function toggleMapVisibility() {
            state.mapVisible = !state.mapVisible;
            localStorage.setItem('mapVisible', state.mapVisible.toString());

            if (state.mapVisible) {
                elements.mapContainer.classList.add('visible');
                elements.toggleMapBtn.textContent = 'Hide Map';
                elements.toggleMapBtn.classList.add('visible');
                // Trigger map resize to ensure proper rendering
                setTimeout(() => {
                    map.invalidateSize();
                    if (state.routeCalculated && state.startLocation && state.deliveryStops.length > 0) {
                         // Re-calculate to restore state and re-render UI correctly
                        const stopsBackup = [...state.deliveryStops];
                        calculateRoute(true);
                        state.deliveryStops = stopsBackup; // Restore original loaded order before re-optimizing
                    } else if (state.startLocation) {
                        map.setView(state.startLocation.coordinates, 15);
                    } else if (state.deliveryStops.length > 0) {
                        const bounds = L.latLngBounds(state.deliveryStops.map(stop => stop.coordinates));
                        map.fitBounds(bounds, { padding: [50, 50] });
                    }
                }, 300);
            } else {
                elements.mapContainer.classList.remove('visible');
                elements.toggleMapBtn.textContent = 'Show Map';
                elements.toggleMapBtn.classList.remove('visible');
            }
        }

function checkLocationPermission() {
            if (!navigator.permissions) {
                // Browser doesn't support Permissions API
                elements.locationStatus.textContent = "Location permission status unavailable";
                elements.locationStatus.className = "location-status location-error";
                elements.requestLocationBtn.style.display = 'inline-block';
                return;
            }

            navigator.permissions.query({name: 'geolocation'}).then(permissionStatus => {
                updatePermissionUI(permissionStatus.state);

                permissionStatus.onchange = () => {
                    updatePermissionUI(permissionStatus.state);
                };
            }).catch(error => {
                console.error("Error checking location permission:", error);
                elements.locationStatus.textContent = "Error checking location permission";
                elements.locationStatus.className = "location-status location-error";
                elements.requestLocationBtn.style.display = 'inline-block';
            });
        }

function updatePermissionUI(state) {
            if (state === 'granted') {
                elements.locationStatus.textContent = "Location permission granted";
                elements.locationStatus.className = "location-status location-success";
                elements.requestLocationBtn.style.display = 'none';
                getCurrentLocation();
            } else if (state === 'prompt') {
                elements.locationStatus.textContent = "Location permission not granted yet";
                elements.locationStatus.className = "location-status";
                elements.requestLocationBtn.style.display = 'inline-block';
            } else if (state === 'denied') {
                elements.locationStatus.textContent = "Location permission denied. Please enable it in browser settings.";
                elements.locationStatus.className = "location-status location-error";
                elements.requestLocationBtn.style.display = 'inline-block';
            }
        }

function getCurrentLocation() {
            elements.locationStatus.textContent = "Getting your current location...";
            elements.locationStatus.className = "location-status location-loading";

            if (!navigator.geolocation) {
                elements.locationStatus.textContent = "Geolocation is not supported by your browser";
                elements.locationStatus.className = "location-status location-error";
                elements.requestLocationBtn.style.display = 'inline-block';
                return;
            }

            navigator.geolocation.getCurrentPosition(
                async (position) => {
                    const { latitude, longitude } = position.coords;

                    // Try to reverse geocode to get address
                    try {
                        const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`);
                        const data = await response.json();

                        let address = "Current Location";
                        if (data.display_name) {
                            address = data.display_name;
                        }

                        state.startLocation = {
                            address: address,
                            coordinates: [latitude, longitude],
                            notes: ''
                        };

                        elements.startLocationInput.value = address;
                        elements.locationStatus.textContent = "Using your current location for calculation";
                        elements.locationStatus.className = "location-status location-success";
                        elements.requestLocationBtn.style.display = 'none';

                        updateMapMarkers();
                        updateCalculateRouteButton();
                        saveState();
                    } catch (error) {
                        // If reverse geocoding fails, just use coordinates
                        state.startLocation = {
                            address: `Current Location (${latitude.toFixed(4)}, ${longitude.toFixed(4)})`,
                            coordinates: [latitude, longitude],
                            notes: ''
                        };

                        elements.startLocationInput.value = state.startLocation.address;
                        elements.locationStatus.textContent = "Using your current coordinates";
                        elements.locationStatus.className = "location-status location-success";
                        elements.requestLocationBtn.style.display = 'none';

                        updateMapMarkers();
                        updateCalculateRouteButton();
                        saveState();
                    }
                },
                (error) => {
                    let errorMessage = "Unable to retrieve your location";
                    switch(error.code) {
                        case error.PERMISSION_DENIED:
                            errorMessage = "Location access denied. Please enable location services in your browser settings.";
                            elements.requestLocationBtn.style.display = 'inline-block';
                            break;
                        case error.POSITION_UNAVAILABLE:
                            errorMessage = "Location information unavailable.";
                            break;
                        case error.TIMEOUT:
                            errorMessage = "Location request timed out.";
                            elements.requestLocationBtn.style.display = 'inline-block';
                            break;
                    }
                    elements.locationStatus.textContent = errorMessage;
                    elements.locationStatus.className = "location-status location-error";
                },
                {
                    enableHighAccuracy: true,
                    timeout: 10000,
                    maximumAge: 0
                }
            );
        }

function init() {
            // Set saved API key if exists
            if (state.apiKey) {
                elements.apiKeyInput.value = state.apiKey;
                updateApiKeyStatus(true);
                loadGoogleMapsSDK(); // Load SDK with the key
            } else {
                updateApiKeyStatus(false);
            }

            // Set saved optimization method
            if (state.optimizationMethod === 'distance') {
                elements.optimizeDistance.checked = true;
            } else if (state.optimizationMethod === 'time') {
                elements.optimizeTime.checked = true;
            } else if (state.optimizationMethod === 'fuel') {
                elements.optimizeFuel.checked = true;
            } else if (state.optimizationMethod === 'roundtrip') {
                elements.roundTrip.checked = true;
            }

            // Set initial map visibility
            if (state.mapVisible) {
                toggleMapVisibility();
            }

            // Set AI training data if exists
            if (state.aiTrainingData) {
                elements.aiTrainingData.value = state.aiTrainingData;
            }

            // Load saved state if exists
            loadSavedState();

            // Check location permission
            checkLocationPermission();

            // Event listeners
            elements.hamburgerMenuBtn.addEventListener('click', toggleSideMenu);
            document.addEventListener('click', (e) => {
                if (elements.sideMenu.classList.contains('visible') && !elements.sideMenu.contains(e.target) && !elements.hamburgerMenuBtn.contains(e.target)) {
                    toggleSideMenu();
                }
            });

            elements.menuItems.forEach(item => {
                item.addEventListener('click', (e) => {
                    e.preventDefault();
                    const tabId = item.dataset.tab;
                    if (tabId) {
                        switchTab(tabId);
                        toggleSideMenu();
                    }
                });
            });

            elements.saveApiKeyBtn.addEventListener('click', saveApiKey);
            elements.startLocationInput.addEventListener('input', debounce(() => handleLocationInput(elements.startLocationInput, elements.startSuggestions, true), 300));
            elements.deliveryLocationInput.addEventListener('input', debounce(() => handleLocationInput(elements.deliveryLocationInput, elements.deliverySuggestions, false), 300));
            elements.addStopBtn.addEventListener('click', addDeliveryStop);
            elements.calculateRouteBtn.addEventListener('click', calculateRoute);
            elements.clearAllBtn.addEventListener('click', clearAll);
            elements.searchPostcodeBtn.addEventListener('click', searchPostcodeForResults);
            elements.toggleMapMenuItem.addEventListener('click', (e) => {
                e.preventDefault();
                toggleMapVisibility();
                toggleSideMenu();
            });
            elements.closeMapBtn.addEventListener('click', toggleMapVisibility);
            elements.requestLocationBtn.addEventListener('click', requestLocationPermission);
            elements.saveNoteBtn.addEventListener('click', saveNote);
            elements.cancelNoteBtn.addEventListener('click', closeNoteModal);
            elements.noteModalClose.addEventListener('click', closeNoteModal);

            // AI Chat event listeners
            elements.aiChatSend.addEventListener('click', sendAiMessage);
            elements.aiChatInput.addEventListener('keypress', (e) => {
                if (e.key === 'Enter') sendAiMessage();
            });

            // Quick action buttons
            document.querySelectorAll('.ai-chat-quick-action').forEach(button => {
                if (button.id !== 'ai-settings-btn') {
                    button.addEventListener('click', (e) => {
                        const prompt = e.target.getAttribute('data-prompt');
                        elements.aiChatInput.value = prompt;
                        sendAiMessage();
                    });
                }
            });

            // AI Settings event listeners
            elements.aiSettingsBtn.addEventListener('click', openAiSettings);
            elements.aiSettingsClose.addEventListener('click', closeAiSettings);
            elements.closeAiSettings.addEventListener('click', closeAiSettings);
            elements.trainAiBtn.addEventListener('click', trainAiWithData);
            elements.scanParcelBtn.addEventListener('click', handleParcelScan);

            // Notification permission banner listeners
            elements.enableNotificationsBtn.addEventListener('click', requestNotificationPermission);
            elements.dismissNotificationsBtn.addEventListener('click', () => {
                elements.notificationBanner.style.display = 'none';
            });

            // Postcode Scanner event listeners
            elements.scanPostcodeBtn.addEventListener('click', startPostcodeScanner);
            elements.scannerModalClose.addEventListener('click', stopPostcodeScanner);
            elements.confirmPostcodeBtn.addEventListener('click', () => {
                elements.postcodeSearchInput.value = elements.scannedPostcodeResult.textContent;
                stopPostcodeScanner();
                elements.searchPostcodeBtn.click(); // Automatically search after confirming
            });
            elements.retryScanBtn.addEventListener('click', () => {
                elements.scannerResultContainer.style.display = 'none';
                elements.scannerStatus.textContent = 'Scanning...';
                startScanInterval();
            });

            // Close modals when clicking outside
            window.addEventListener('click', (event) => {
                if (event.target === elements.noteModal) {
                    closeNoteModal();
                }
                if (event.target === elements.aiSettingsModal) {
                    closeAiSettings();
                }
            });

            // Route optimization selection
            elements.optimizeDistance.addEventListener('change', () => {
                if (elements.optimizeDistance.checked) {
                    state.optimizationMethod = 'closest-to-start';
                    localStorage.setItem('routeOptimizationMethod', 'closest-to-start');
                }
            });
            elements.optimizeTime.addEventListener('change', () => {
                if (elements.optimizeTime.checked) {
                    state.optimizationMethod = 'time';
                    localStorage.setItem('routeOptimizationMethod', 'time');
                }
            });
            elements.optimizeFuel.addEventListener('change', () => {
                if (elements.optimizeFuel.checked) {
                    state.optimizationMethod = 'fuel';
                    localStorage.setItem('routeOptimizationMethod', 'fuel');
                }
            });
            elements.optimizeCluster.addEventListener('change', () => {
                if (elements.optimizeCluster.checked) {
                    state.optimizationMethod = 'cluster';
                    localStorage.setItem('routeOptimizationMethod', 'cluster');
                }
            });
            elements.roundTrip.addEventListener('change', () => {
                if (elements.roundTrip.checked) {
                    state.optimizationMethod = 'roundtrip';
                    localStorage.setItem('routeOptimizationMethod', 'roundtrip');
                }
            });

            elements.deliveryLocationInput.addEventListener('input', () => {
                 if (state.selectedDeliveryLocation && elements.deliveryLocationInput.value !== state.selectedDeliveryLocation.address) {
                     state.selectedDeliveryLocation = null;
                     elements.addStopBtn.disabled = true;
                 }
            });

            elements.startLocationInput.addEventListener('input', () => {
                 if (state.startLocation && elements.startLocationInput.value !== state.startLocation.address) {
                     state.startLocation = null;
                     updateMapMarkers();
                     updateCalculateRouteButton();
                 }
            });

            document.addEventListener('click', (e) => {
                 if (!elements.startLocationInput.contains(e.target) && !elements.startSuggestions.contains(e.target)) {
                    elements.startSuggestions.style.display = 'none';
                }
                 if (!elements.deliveryLocationInput.contains(e.target) && !elements.deliverySuggestions.contains(e.target)) {
                    elements.deliverySuggestions.style.display = 'none';
                }
                 if (!elements.postcodeSearchInput.contains(e.target) && !elements.searchPostcodeBtn.contains(e.target) && !elements.addressResults.contains(e.target)) {
                    elements.addressResults.style.display = 'none';
                }
            });

            elements.viewLiveRouteBtn.addEventListener('click', () => {
                switchTab('route-tab');
            });

            elements.startStopSelect.addEventListener('change', (e) => {
                state.startStopOverride = e.target.value || null;
                saveState();
            });

            elements.endStopSelect.addEventListener('change', (e) => {
                state.endStopOverride = e.target.value || null;
                saveState();
            });

            updateCalculateRouteButton();
            updateViewLiveRouteButton();
        }

function checkNotificationPermission() {
            if (!('Notification' in window)) {
                console.log("This browser does not support desktop notification");
                return;
            }

            if (Notification.permission === 'default') {
                elements.notificationBanner.style.display = 'block';
            } else {
                elements.notificationBanner.style.display = 'none';
            }
        }

function requestNotificationPermission() {
            elements.notificationBanner.style.display = 'none';
            if (!('Notification' in window)) return;

            Notification.requestPermission().then(permission => {
                if (permission === 'granted') {
                    new Notification('paRoad Notifications', {
                        body: 'You will now receive updates on your next stop directly on your device.',
                        tag: 'paRoad-setup'
                    });
                }
            });
        }

async function showCurrentStopNotification(stop) {
            if (!('Notification' in window) || Notification.permission !== 'granted') {
                return;
            }

            const title = `Next Stop: ${stop.address.split(',')[0]}`;
            const options = {
                body: stop.address,
                tag: 'current-stop-notification', // Replaces any existing notification with the same tag
                renotify: true, // Notifies user even if a notification with same tag exists
                requireInteraction: true, // Makes notification persistent on some platforms (e.g., desktop)
            };

            try {
                // Ensure service worker is ready before showing notification
                const registration = await navigator.serviceWorker.getRegistration();
                if (registration) {
                    await registration.showNotification(title, options);
                } else {
                     new Notification(title, options); // Fallback for when SW not available
                }
            } catch (err) {
                console.error('Notification error:', err);
                new Notification(title, options); // Fallback to basic notification
            }
        }

