function optimizeRoute(stops, method, startingPoint) {
            // This function implements a nearest-neighbor heuristic for the Traveling Salesperson Problem.
            // It provides a much more efficient route than simple sorting.
            if (!startingPoint || stops.length < 2) {
                if (stops.length === 1 && startingPoint) {
                    return stops;
                }
                return stops;
            }

            // A new simple sort option for users who prefer it.
            if (method === 'closest-to-start') {
                 // Sorts stops based on their direct distance from the starting point.
                return stops.sort((a, b) => {
                    const distA = calculateDistance(startingPoint.coordinates, a.coordinates);
                    const distB = calculateDistance(startingPoint.coordinates, b.coordinates);
                    return distA - distB;
                });
            }

            if (method === 'cluster') {
                return optimizeByClustering(stops, startingPoint);
            }

            // Default (time) and roundtrip: Nearest Neighbor algorithm
            let unvisitedStops = [...stops];
            let orderedStops = [];
            let currentPoint = startingPoint;

            while (unvisitedStops.length > 0) {
                let nearestStopIndex = -1;
                let minDistance = Infinity;

                // Find the closest unvisited stop to the current point in the route
                for (let i = 0; i < unvisitedStops.length; i++) {
                    const distance = calculateDistance(currentPoint.coordinates, unvisitedStops[i].coordinates);

                    if (distance < minDistance) {
                        minDistance = distance;
                        nearestStopIndex = i;
                    }
                }

                // Add the nearest stop to our ordered list and remove it from unvisited
                const nearestStop = unvisitedStops.splice(nearestStopIndex, 1)[0];
                orderedStops.push(nearestStop);

                // The new current point is the stop we just added
                currentPoint = nearestStop;
            }

            // For 'fuel' option, apply 2-opt refinement for a better route
            if (method === 'fuel') {
                return twoOptRefinement(orderedStops, startingPoint);
            }

            return orderedStops;
        }

function twoOptRefinement(route, startingPoint) {
            if (route.length < 2) return route;

            let improved = true;
            let tour = [startingPoint, ...route];

            while (improved) {
                improved = false;
                for (let i = 0; i < tour.length - 2; i++) {
                    for (let j = i + 2; j < tour.length - 1; j++) {
                        const p_i = tour[i];
                        const p_i1 = tour[i + 1];
                        const p_j = tour[j];
                        const p_j1 = tour[j + 1];

                        const original_dist = calculateDistance(p_i.coordinates, p_i1.coordinates) + calculateDistance(p_j.coordinates, p_j1.coordinates);
                        const new_dist = calculateDistance(p_i.coordinates, p_j.coordinates) + calculateDistance(p_i1.coordinates, p_j1.coordinates);

                        if (new_dist < original_dist) {
                            // Reverse the segment from i+1 to j
                            const segment = tour.slice(i + 1, j + 1);
                            segment.reverse();
                            tour = [...tour.slice(0, i + 1), ...segment, ...tour.slice(j + 1)];
                            improved = true;
                        }
                    }
                    if (improved) break; // Restart scan with the improved route
                }
            }

            return tour.slice(1); // Return just the stops, without the starting point
        }

function optimizeByClustering(stops, startingPoint) {
            if (stops.length < 3) return stops; // Not enough points to cluster effectively
            // Simplified K-Means clustering
            // 1. Determine K (number of clusters)
            const k = Math.min(Math.max(2, Math.floor(Math.sqrt(stops.length / 2))), 5); // Heuristic for K, capped at 5

            // 2. Initialize centroids randomly from stops
            let centroids = stops.slice(0, k).map(s => s.coordinates);
            let clusters = Array.from({ length: k }, () => []);

            for (let iter = 0; iter < 10; iter++) { // Iterate a few times to stabilize clusters
                // 3. Assign stops to the closest centroid
                clusters = Array.from({ length: k }, () => []);
                stops.forEach(stop => {
                    let minDistance = Infinity;
                    let closestCentroidIndex = 0;
                    centroids.forEach((centroid, index) => {
                        const dist = calculateDistance(stop.coordinates, centroid);
                        if (dist < minDistance) {
                            minDistance = dist;
                            closestCentroidIndex = index;
                        }
                    });
                    clusters[closestCentroidIndex].push(stop);
                });

                // 4. Recalculate centroids
                centroids = clusters.map(cluster => {
                    if (cluster.length === 0) return [0, 0]; // Should not happen with good data
                    const sum = cluster.reduce((acc, stop) => [acc[0] + stop.coordinates[0], acc[1] + stop.coordinates[1]], [0, 0]);
                    return [sum[0] / cluster.length, sum[1] / cluster.length];
                }).filter(c => c[0] !== 0); // Filter out empty clusters' centroids
            }

            // 5. Order clusters by distance from start
            clusters.sort((a, b) => {
                if (a.length === 0) return 1;
                if (b.length === 0) return -1;
                const centroidA = centroids[clusters.indexOf(a)];
                const centroidB = centroids[clusters.indexOf(b)];
                return calculateDistance(startingPoint.coordinates, centroidA) - calculateDistance(startingPoint.coordinates, centroidB);
            });

            // 6. For each cluster, find the optimal internal path (using Nearest Neighbor)
            let finalRoute = [];
            let lastPoint = startingPoint;

            clusters.forEach(cluster => {
                if (cluster.length > 0) {
                    let unvisited = [...cluster];
                    let orderedCluster = [];
                    // Find the best entry point into the cluster from the last point of the previous cluster
                    let bestEntryPointIndex = 0;
                    let minEntryDistance = Infinity;
                    unvisited.forEach((stop, index) => {
                        const dist = calculateDistance(lastPoint.coordinates, stop.coordinates);
                        if (dist < minEntryDistance) {
                            minEntryDistance = dist;
                            bestEntryPointIndex = index;
                        }
                    });
                    let currentPoint = unvisited.splice(bestEntryPointIndex, 1)[0];
                    orderedCluster.push(currentPoint);

                    // Optimize within the cluster
                    while (unvisited.length > 0) {
                        let nearestStopIndex = -1;
                        let minDistance = Infinity;
                        for (let i = 0; i < unvisited.length; i++) {
                            const distance = calculateDistance(currentPoint.coordinates, unvisited[i].coordinates);
                            if (distance < minDistance) {
                                minDistance = distance;
                                nearestStopIndex = i;
                            }
                        }
                        const nearestStop = unvisited.splice(nearestStopIndex, 1)[0];
                        orderedCluster.push(nearestStop);
                        currentPoint = nearestStop;
                    }
                    finalRoute.push(...orderedCluster);
                    lastPoint = orderedCluster[orderedCluster.length - 1]; // Update last point for next cluster
                }
            });

            return finalRoute;
        }

function calculateDistance(coord1, coord2) {
            if (!coord1 || !coord2) return 0; // Guard against undefined coordinates
            const R = 6371; // Radius of the Earth in km
            const dLat = (coord2[0] - coord1[0]) * Math.PI / 180;
            const dLon = (coord2[1] - coord1[1]) * Math.PI / 180;
            const a = 
                Math.sin(dLat/2) * Math.sin(dLat/2) +
                Math.cos(coord1[0] * Math.PI / 180) * Math.cos(coord2[0] * Math.PI / 180) * 
                Math.sin(dLon/2) * Math.sin(dLon/2);
            const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
            return R * c;
        }

async function calculateRoute(isReload = false) {
            updateCalculateRouteButton();

            if (elements.calculateRouteBtn.disabled && !isReload) {
                alert("Cannot calculate route. Please add at least one stop.");
                return;
            }

            elements.calculateRouteBtn.textContent = 'Getting location...';
            elements.calculateRouteBtn.disabled = true;

            try {
                const currentLocation = await getUpdatedCurrentLocation();
                state.startLocation = currentLocation;
                elements.startLocationInput.value = currentLocation.address;
                elements.locationStatus.textContent = "Using your current location for calculation";
                elements.locationStatus.className = "location-status location-success";
            } catch (error) {
                alert(`Could not get current location: ${error}. Please ensure location services are enabled.`);
                elements.calculateRouteBtn.textContent = 'Calculate Route';
                updateCalculateRouteButton();
                return;
            }

            elements.calculateRouteBtn.textContent = 'Calculating...';

            // Separate completed stops from incomplete ones
            const completedStops = state.deliveryStops.filter(stop => stop.completed);
            let incompleteStops = state.deliveryStops.filter(stop => !stop.completed);

            if (incompleteStops.length === 0) {
                alert("All stops are marked as complete. There's no new route to calculate.");
                clearRouteVisualization();
                state.routeCalculated = false;
                elements.calculateRouteBtn.textContent = 'Calculate Route';
                updateCalculateRouteButton();
                return;
            }

            // --- NEW LOGIC FOR START/END STOPS ---
            let startStopForRoute = null;

            // If a start stop is selected, use it as the start location
            if (state.startStopOverride) {
                const startIdx = state.deliveryStops.findIndex(s => s.address === state.startStopOverride);
                if (startIdx > -1) {
                    startStopForRoute = state.deliveryStops[startIdx];
                    state.startLocation = startStopForRoute;
                    elements.startLocationInput.value = state.startLocation.address;
                    elements.locationStatus.textContent = `Starting route from: ${state.startLocation.address.split(',')[0]}`;
                    elements.locationStatus.className = "location-status location-success";
                }
            }

            // Only get current location if no start stop override is selected or found
            if (!startStopForRoute) {
                try {
                    const currentLocation = await getUpdatedCurrentLocation();
                    state.startLocation = currentLocation;
                    elements.startLocationInput.value = currentLocation.address;
                    elements.locationStatus.textContent = "Using your current location for calculation";
                    elements.locationStatus.className = "location-status location-success";
                } catch (error) {
                    alert(`Could not get current location: ${error}. Please ensure location services are enabled.`);
                    elements.calculateRouteBtn.textContent = 'Calculate Route';
                    updateCalculateRouteButton();
                    return;
                }
            }

            elements.calculateRouteBtn.textContent = 'Calculating...';

            // Handle start/end constraints
            let endStop = null;
            let stopsToOptimize = [...incompleteStops];

            if (state.endStopOverride) {
                const endIdx = stopsToOptimize.findIndex(s => s.address === state.endStopOverride);
                if (endIdx > -1) {
                    // It also shouldn't be the same as the start stop if one was chosen
                    if (!startStopForRoute || (startStopForRoute.address !== state.endStopOverride)) {
                        endStop = stopsToOptimize.splice(endIdx, 1)[0];
                    }
                }
            }

            const optimizationStartPoint = state.startLocation; // Always use state.startLocation as it's been set correctly

            // Optimize the route using the remaining incomplete stops.
            const reorderedIntermediateStops = optimizeRoute(stopsToOptimize, state.optimizationMethod, optimizationStartPoint);

            let reorderedIncompleteStops = [];
            reorderedIncompleteStops.push(...reorderedIntermediateStops);
            if (endStop) {
                reorderedIncompleteStops.push(endStop);
            }

            // Update state.deliveryStops with the new optimized order, keeping completed stops at the end.
            state.deliveryStops = [...reorderedIncompleteStops, ...completedStops];

            // Update the sidebar list to reflect the new order
            updateDeliveryStopsUI();

            // Prepare points for route summary and polyline.
            let routePointsForDisplay = [state.startLocation, ...reorderedIncompleteStops];

            // For round trip, add the start location at the end for display purposes
            if (state.optimizationMethod === 'roundtrip' && state.startLocation) {
                routePointsForDisplay.push({
                    ...state.startLocation,
                    address: `Return to: ${state.startLocation.address}`,
                    isReturn: true
                });
            }

            const totalDistance = calculateTotalDistance(routePointsForDisplay);

            let detailsText = `Optimized route (${state.optimizationMethod}) with ${reorderedIncompleteStops.length} stop${reorderedIncompleteStops.length === 1 ? '' : 's'}. `;
            detailsText += `Total approx distance: ${totalDistance.toFixed(1)} km.`;

            if (state.optimizationMethod === 'roundtrip') {
                detailsText += " (Round trip back to start)";
            }

            elements.routeDetails.textContent = detailsText;

            updateRouteSummaryUI(routePointsForDisplay); // Pass the full set of points for summary
            drawRouteLines(routePointsForDisplay);       // Pass the full set of points for polyline
            updateMapMarkers(); // Update map markers, which will use the reordered state.deliveryStops

            state.routeCalculated = true;
            state.currentStopIndex = 0; // Reset to first stop when calculating new route
            updateCurrentStopIndex(); // Set to first incomplete stop
            saveState();

            if (!isReload) {
                addAiMessage("Route calculated! Ask me for loading advice or reminders.");
            }
            elements.calculateRouteBtn.textContent = 'Calculate Route';
            updateCalculateRouteButton();
            updateViewLiveRouteButton();
        }

function updateRouteSummaryUI(routePoints) {
            if (!routePoints) { // If called without specific points (e.g., on completion toggle)
                routePoints = [state.startLocation, ...state.deliveryStops];
                if (state.optimizationMethod === 'roundtrip' && state.startLocation) {
                    routePoints.push({
                        ...state.startLocation,
                        address: `Return to: ${state.startLocation.address}`,
                        isReturn: true
                    });
                }
            }

            elements.routeSequence.innerHTML = '';

            let stopCounter = 0;
            routePoints.forEach((point, index) => {
                if (!point || !point.address || !Array.isArray(point.coordinates) || point.coordinates.length !== 2) return;

                const li = document.createElement('li');

                // Find the original stop object from state.deliveryStops to get its current properties
                // This is crucial because `routePoints` can be a temporary, reordered list
                const originalStopIndexInState = state.deliveryStops.findIndex(s => s.address === point.address);
                const isDeliveryStop = !point.isReturn && index > 0;
                const actualPointReference = isDeliveryStop && originalStopIndexInState > -1 
                    ? state.deliveryStops[originalStopIndexInState]
                    : state.startLocation;

                if (actualPointReference && actualPointReference.completed) {
                    li.className = 'completed';
                }

                // Highlight if it's the current stop
                if (isDeliveryStop && originalStopIndexInState === state.currentStopIndex) {
                    li.classList.add('current-stop');
                }

                const textSpan = document.createElement('span');
                let label;
                if (index === 0) {
                    label = "Start";
                } else if (point.isReturn) {
                    label = "Return";
                } else {
                    stopCounter++;
                    label = `Stop ${stopCounter}`;
                }

                textSpan.innerHTML = `<b>${label}:</b> ${point.address}${actualPointReference.notes ? `<br><small style="color:#666;">Note: ${actualPointReference.notes}</small>` : ''}`;
                textSpan.title = point.address;
                li.appendChild(textSpan);

                const buttonContainer = document.createElement('div');
                buttonContainer.style.display = 'flex';
                buttonContainer.style.gap = '5px';

                // Complete button
                const completeButton = document.createElement('button');
                completeButton.textContent = (actualPointReference && actualPointReference.completed) ? 'âœ“' : 'â—‹';
                completeButton.title = (actualPointReference && actualPointReference.completed) ? 'Mark as incomplete' : 'Mark as complete';
                completeButton.className = 'complete-btn';
                completeButton.addEventListener('click', () => {
                     if (isDeliveryStop && originalStopIndexInState !== -1) {
                        toggleStopCompletion(originalStopIndexInState);
                    } else if (index === 0) { // It's the start location
                         state.startLocation.completed = !state.startLocation.completed;
                         updateCurrentStopIndex();
                         updateAllUIs();
                         saveState();
                    }
                });
                buttonContainer.appendChild(completeButton);

                // Note button
                const noteButton = document.createElement('button');
                noteButton.textContent = 'ðŸ“';
                noteButton.classList.add('note-btn');
                noteButton.title = 'Edit notes';
                noteButton.addEventListener('click', () => {
                    if (isDeliveryStop && originalStopIndexInState !== -1) {
                        openNoteModal(originalStopIndexInState);
                    } else { // Start or Return point
                        openNoteModal('start');
                    }
                });
                buttonContainer.appendChild(noteButton);

                // Navigation button
                const [lat, lng] = point.coordinates;
                if (typeof lat === 'number' && !isNaN(lat) && typeof lng === 'number' && !isNaN(lng)) {
                    const googleMapsUrl = `https://www.google.com/maps/dir/?api=1&destination=${lat},${lng}`;

                    const navButton = document.createElement('button');
                    navButton.textContent = 'Navigate';
                    navButton.classList.add('navigate-btn');
                    navButton.title = `Open navigation to ${label} in Google Maps`;
                    navButton.addEventListener('click', async () => {
                        // Check if it's a delivery stop (not start or return)
                        if (isDeliveryStop && originalStopIndexInState !== -1) {
                            // 1. Update current stop index
                            state.currentStopIndex = originalStopIndexInState;

                            // 2. Update all UIs to reflect the new current stop
                            updateAllUIs();

                            // 3. Save the state
                            saveState();

                            // 4. Show native notification for the new current stop
                            await showCurrentStopNotification(actualPointReference);
                        }

                        // 5. Open Google Maps for navigation
                        window.open(googleMapsUrl, '_blank');
                    });
                    buttonContainer.appendChild(navButton);
                } else {
                    const noNavSpan = document.createElement('span');
                    noNavSpan.style.fontSize = '0.8em';
                    noNavSpan.style.fontStyle = 'italic';
                    noNavSpan.style.marginLeft = '10px';
                    noNavSpan.textContent = '(Nav not available)';
                    buttonContainer.appendChild(noNavSpan);
                }

                li.appendChild(buttonContainer);
                elements.routeSequence.appendChild(li);
            });

            elements.routeSummary.style.display = 'block';
            handleLiveRouteSearch(); // Apply search filter after updating the list
        }

function calculateTotalDistance(points) {
            if (!points || points.length < 2) return 0;

            let totalDistance = 0;
            for (let i = 0; i < points.length - 1; i++) {
                if (points[i] && points[i+1] && points[i].coordinates && points[i+1].coordinates) {
                    totalDistance += calculateDistance(points[i].coordinates, points[i+1].coordinates);
                }
            }
            return totalDistance;
        }

function clearRouteVisualization() {
            routeLines.forEach(line => {
                if (map.hasLayer(line)) {
                     map.removeLayer(line);
                }
            });
            routeLines = [];
            elements.routeSummary.style.display = 'none';
            elements.routeSequence.innerHTML = '';
            elements.routeDetails.textContent = '';
        }

function updateMapMarkers() {
            markers.forEach(marker => {
                if (map.hasLayer(marker)) {
                    map.removeLayer(marker);
                }
            });
            markers = [];
            let allPoints = [];

            // Start location marker
            if (state.startLocation && state.startLocation.coordinates) {
                const startIconHtml = `<div class="numbered-marker start-marker ${state.startLocation.completed ? 'completed-marker' : ''}">S</div>`;
                const startDivIcon = L.divIcon({
                    html: startIconHtml,
                    className: '', // Classes are in the HTML string
                    iconSize: [28, 28],
                    iconAnchor: [14, 28] // Anchor at bottom-center
                });
                const startMarker = L.marker(state.startLocation.coordinates, { icon: startDivIcon })
                    .addTo(map)
                    .bindPopup(`<b>Start Location</b><br>${state.startLocation.address}${state.startLocation.notes ? `<br><br>Notes: ${state.startLocation.notes}` : ''}`);
                markers.push(startMarker);
                allPoints.push(state.startLocation.coordinates);
            }

            // Delivery stop markers
            state.deliveryStops.forEach((stop, index) => {
                if (!stop.coordinates) return;

                let markerClasses = "numbered-marker";
                if (stop.completed) markerClasses += " completed-marker";
                if (index === state.currentStopIndex) markerClasses += " current-marker";

                let iconHtml = `<div class="${markerClasses}">${index + 1}</div>`;
                const stopDivIcon = L.divIcon({
                    html: iconHtml,
                    className: '', // Classes are in the HTML string
                    iconSize: [28, 28],
                    iconAnchor: [14, 28] // Anchor at bottom-center
                });

                const stopMarker = L.marker(stop.coordinates, { icon: stopDivIcon })
                    .addTo(map)
                    .bindPopup(`<b>Stop ${index + 1}</b><br>${stop.address}${stop.notes ? `<br><br>Notes: ${stop.notes}` : ''}`);
                markers.push(stopMarker);
                allPoints.push(stop.coordinates);
            });

            if (allPoints.length > 0) {
                try {
                    const bounds = L.latLngBounds(allPoints);
                    // Only fit bounds if not a calculated route or if only one point,
                    // otherwise drawRouteLines will handle fitting.
                    if (!state.routeCalculated || allPoints.length <= 1) {
                        map.fitBounds(bounds, { padding: [50, 50] });
                    }
                } catch(e) {
                    if (allPoints.length === 1) map.setView(allPoints[0], 15);
                }
            } else {
                map.setView([54.5, -4], 6); // Default view if no points
            }
        }

function drawRouteLines(points) {
            routeLines.forEach(line => {
                if (map.hasLayer(line)) {
                     map.removeLayer(line);
                }
            });
            routeLines = [];

            const coords = points
                .filter(p => p && p.coordinates)
                .map(p => p.coordinates)
                .filter(c => Array.isArray(c) && c.length === 2 && !isNaN(c[0]) && !isNaN(c[1]));

            if (coords.length < 2) return;

            try {
                const routePolyline = L.polyline(coords, {
                    color: '#2196F3',
                    weight: 4,
                    opacity: 0.8,
                }).addTo(map);
                routeLines.push(routePolyline);
                map.fitBounds(routePolyline.getBounds(), { padding: [60, 60] });
            } catch (e) {
                console.error("Error drawing polyline:", e, coords);
                alert("An error occurred while drawing the route on the map.");
                elements.routeSummary.style.display = 'none';
                state.routeCalculated = false;
            }
        }

function switchTab(tabId) {
            elements.tabPanes.forEach(pane => {
                pane.classList.remove('active');
            });
            elements.menuItems.forEach(item => {
                if(item.dataset.tab === tabId) {
                    item.classList.add('active');
                } else {
                    item.classList.remove('active');
                }
            });

            document.getElementById(tabId).classList.add('active');

            // If switching to AI tab, remove notification
            if (tabId === 'ai-tab') {
                state.hasNewAiNotification = false;
                updateAiNotificationBadge();
                setTimeout(() => {
                    elements.aiChatBody.scrollTop = elements.aiChatBody.scrollHeight;
                }, 100);
            }
        }

function updateCurrentStopIndex() {
            const firstIncompleteIndex = state.deliveryStops.findIndex(stop => !stop.completed);
            state.currentStopIndex = firstIncompleteIndex !== -1 ? firstIncompleteIndex : state.deliveryStops.length;
        }

function updateAllUIs() {
            updateDeliveryStopsUI();
            updateMapMarkers();
            if(state.routeCalculated) {
                // When re-rendering summary, ensure current stop is highlighted correctly
                updateRouteSummaryUI();
            }
        }

function updateViewLiveRouteButton() {
            if (elements.viewLiveRouteBtn) {
                elements.viewLiveRouteBtn.disabled = !state.routeCalculated;
            }
        }

function updateCalculateRouteButton() {
            const stopsExist = state.deliveryStops.length > 0;
