                optionEnd.textContent = stop.address;
                endSelect.appendChild(optionEnd);
            });

            startSelect.value = currentStartVal;
            endSelect.value = currentEndVal;
        }

async function startPostcodeScanner() {
            if (state.isScanning) return;
            state.isScanning = true;

            elements.postcodeScannerModal.style.display = 'flex';
            elements.scannerStatus.textContent = 'Starting camera...';
            elements.scannerResultContainer.style.display = 'none';
            elements.scannerVideo.style.display = 'block';

            try {
                if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
                    throw new Error("Camera access is not supported by your browser.");
                }
                state.videoStream = await navigator.mediaDevices.getUserMedia({ 
                    video: { 
                        facingMode: 'environment',
                        width: { ideal: 1280 },
                        height: { ideal: 720 }
                    } 
                });
                elements.scannerVideo.srcObject = state.videoStream;
                await elements.scannerVideo.play();
                elements.scannerStatus.textContent = 'Scanning...';
                startScanInterval();
            } catch (err) {
                console.error("Error accessing camera: ", err);
                elements.scannerStatus.textContent = `Error: ${err.message}. Please check permissions.`;
                state.isScanning = false;
            }
        }

function stopPostcodeScanner() {
            if (state.scanIntervalId) {
                clearInterval(state.scanIntervalId);
                state.scanIntervalId = null;
            }
            if (state.videoStream) {
                state.videoStream.getTracks().forEach(track => track.stop());
                state.videoStream = null;
            }
            elements.scannerVideo.srcObject = null;
            elements.postcodeScannerModal.style.display = 'none';
            state.isScanning = false;
        }

function startScanInterval() {
            if (state.scanIntervalId) {
                clearInterval(state.scanIntervalId);
            }
            state.scanIntervalId = setInterval(scanFrameForPostcode, 2000); // Scan every 2 seconds
        }

async function scanFrameForPostcode() {
            if (!state.isScanning || elements.scannerVideo.readyState < 2) {
                return;
            }

            const canvas = elements.scannerCanvas;
            const video = elements.scannerVideo;
            const context = canvas.getContext('2d');

            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
            context.drawImage(video, 0, 0, canvas.width, canvas.height);

            const imageDataUrl = canvas.toDataURL('image/jpeg', 0.8);

            try {
                const completion = await websim.chat.completions.create({
                    messages: [{
                        role: 'user',
                        content: [{
                                type: 'text',
                                text: `Analyze this image and extract any valid full UK postcode (e.g., SW1A 0AA, M1 1AE, G1 1RD, BT1 5GS). A full UK postcode has an outward code and an inward code separated by a space. The outward code is 2-4 characters long, and the inward code is always 3 characters long. Respond directly with JSON, following this JSON schema, and no other text. If no valid postcode is found, return an empty string for "postcode".\n\n{ "postcode": "string" }`,
                            },
                            {
                                type: 'image_url',
                                image_url: { url: imageDataUrl },
                            },
                        ],
                    }, ],
                    json: true,
                });

                const result = JSON.parse(completion.content);

                if (result.postcode) {
                    clearInterval(state.scanIntervalId); // Stop scanning
                    elements.scannedPostcodeResult.textContent = result.postcode;
                    elements.scannerResultContainer.style.display = 'block';
                    elements.scannerStatus.textContent = 'Postcode Found!';
                } else {
                    elements.scannerStatus.textContent = 'Scanning... No postcode detected yet.';
                }
            } catch (error) {
                console.error('Error during postcode scan:', error);
                elements.scannerStatus.textContent = 'Error during scan. Retrying...';
            }
        }

function loadGoogleMapsSDK() {
            if (window.google && window.google.maps) {
                console.log("Google Maps SDK already loaded.");
                initializeGoogleServices();
                return;
            }
            if (document.getElementById('google-maps-sdk').src) {
                console.log("Google Maps SDK is already loading.");
                return;
            }

            const script = document.getElementById('google-maps-sdk');
            script.src = `https://maps.googleapis.com/maps/api/js?key=${state.apiKey}&libraries=places&callback=initializeGoogleServices`;
        }

function initializeGoogleServices() {
            if (window.google && window.google.maps && window.google.maps.places) {
                autocompleteService = new google.maps.places.AutocompleteService();
                // We need a map instance to initialize PlacesService, but we can use a dummy div.
                const dummyDiv = document.createElement('div');
                placesService = new google.maps.places.PlacesService(dummyDiv);
                geocoder = new google.maps.Geocoder();
                console.log("Google Maps services initialized.");
            } else {
                console.error("Google Maps SDK failed to load correctly.");
                alert("Could not initialize Google Maps services. Address search may not work.");
            }
        }

function updateApiKeyStatus(isSet) {
            if (isSet) {
                elements.apiKeyStatus.textContent = 'Saved';
                elements.apiKeyStatus.classList.remove('not-set');
                elements.apiKeyStatus.classList.add('saved');
            } else {
                elements.apiKeyStatus.textContent = 'Not set';
                elements.apiKeyStatus.classList.remove('saved');
                elements.apiKeyStatus.classList.add('not-set');
            }
        }

function saveApiKey() {
            const newApiKey = elements.apiKeyInput.value.trim();
            if (newApiKey) {
                state.apiKey = newApiKey;
                localStorage.setItem('googleMapsApiKey', newApiKey);
                updateApiKeyStatus(true);
                alert('API Key saved successfully!');
                if (!window.google || !window.google.maps) {
                    loadGoogleMapsSDK();
                } else {
                    // If SDK is loaded, re-initialize services with the new key.
                    initializeGoogleServices();
                }
            } else {
                alert('Please enter an API key.');
            }
        }
